// frontend/src/app/auth.service.ts

import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { environment } from '../environments/environment';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  private apiUrl = environment.apiUrl;

  constructor(private http: HttpClient) {}

  private getToken(): string | null {
    return localStorage.getItem('auth_token');
  }

  private setToken(token: string): void {
    localStorage.setItem('auth_token', token);
  }

  private removeToken(): void {
    localStorage.removeItem('auth_token');
  }

  async login(username: string, password: string): Promise<boolean> {
    const url = `${this.apiUrl}/dj-rest-auth/login/`;
    try {
      const response: any = await this.http.post(url, { username, password }).toPromise();
      this.setToken(response.key);
      return true;
    } catch (error) {
      console.error('Login failed:', error);
      return false;
    }
  }

  async logout(): Promise<void> {
    const url = `${this.apiUrl}/dj-rest-auth/logout/`;
    try {
      const token = this.getToken();
      if (!token) {
        throw new Error('No token found');
      }
      const headers = new HttpHeaders({ 'Authorization': `Token ${token}` });
      await this.http.post(url, {}, { headers }).toPromise();
      this.removeToken();
    } catch (error) {
      console.error('Logout failed:', error);
    }
  }

  getAuthHeaders(): HttpHeaders {
    const token = this.getToken();
    if (token) {
      return new HttpHeaders({ 'Authorization': `Token ${token}` });
    } else {
      return new HttpHeaders();
    }
  }
}
