// frontend/src/app/auth.service.ts

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { CookieService } from 'ngx-cookie-service';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  private apiUrl = 'http://localhost:8000';
  private tokenCookieKey = 'authToken';

  constructor(private http: HttpClient, private cookieService: CookieService) {}

  login(username: string, password: string) {
    const url = `${this.apiUrl}/dj-rest-auth/login/`;
    return this.http.post(url, { username, password }).toPromise();
  }

  setToken(token: string) {
    this.cookieService.set(this.tokenCookieKey, token);
  }

  getToken(): string | null {
    return this.cookieService.get(this.tokenCookieKey);
  }

  removeToken() {
    this.cookieService.delete(this.tokenCookieKey);
  }
}
